import * as React from 'react';
import styled from 'styled-components';

function IdInputPage() {
  return (
    <StyledRegistrationPage>
      <HeaderImage
        loading="lazy"
        src="https://cdn.builder.io/api/v1/image/assets/TEMP/819a2a6e9305afd6dc5707053bfcbb80751414d4628f0b133b8ea6b1ebd6ffb0?apiKey=a65641faa3d54339891445c030384eb2&"
      />
      <MainContent>
        <UserInfoSection>
          <Title>회원가입</Title>
        </UserInfoSection>
        <Instruction>아이디를 입력해주세요.</Instruction>
        <InputField placeholder="ID" />
        <StepInfo>1 of 3</StepInfo>
        <ProgressSection>
          <ProgressBar />
        </ProgressSection>
        <NextButton>다음</NextButton>
        <FooterIndicator />
      </MainContent>
    </StyledRegistrationPage>
  );
}

const StyledRegistrationPage = styled.section`
  background-color: #fff;
  display: flex;
  flex-direction: column;
  align-items: center;
  max-width: 480px;
  width: 100%;
  padding-bottom: 26px;
  margin: 0 auto;
`;

const MainContent = styled.main`
  display: flex;
  flex-direction: column;
  align-items: center;
  width: 100%;
`;

const HeaderImage = styled.img`
  aspect-ratio: 8.33;
  object-fit: cover;
  object-position: center;
  width: 100%;
`;

const UserInfoSection = styled.header`
  display: flex;
  flex-direction: column;
  width: 100%;
  align-items: center;
  color: var(--Text-Primary, #14304a);
  font-weight: 600;
`;

const Title = styled.h1`
  font-family: Inter, sans-serif;
  margin-top: 20px;
  text-align: center;
`;

const Instruction = styled.p`
  letter-spacing: 0.14px;
  margin-top: 27px;
  font-size: 28px;
  font-family: Inter, sans-serif;
  text-align: center;
`;

const InputField = styled.input`
  font-family: Inter, sans-serif;
  font-size: 18px;
  border: none;
  border-bottom: 2px solid #000;
  background-color: transparent;
  margin-top: 12px;
  padding: 8px 0;
  text-align: center;

  ::placeholder {
    color: #888;
  }

  &:focus {
    outline: none;
  }
`;

const StepInfo = styled.p`
  text-align: center;
  margin-top: 364px;
  white-space: nowrap;
  font-size: 13px;
  font-family: Inter, sans-serif;
`;

const ProgressSection = styled.section`
  border-radius: 50px;
  background-color: #f0f0f0;
  display: flex;
  margin-top: 14px;
  width: 100%;
  max-width: 327px;
  justify-content: center;
`;

const ProgressBar = styled.div`
  border-radius: 50px;
  background-color: #000;
  height: 8px;
  width: 50%; /* Example progress */
`;

const NextButton = styled.button`
  align-items: center;
  border-radius: 32px;
  background-color: #000;
  margin-top: 25px;
  width: 100%;
  max-width: 327px;
  justify-content: center;
  color: #fff;
  white-space: nowrap;
  text-align: center;
  letter-spacing: 0.1px;
  padding: 19px 60px;
  font: 700 15px Inter, sans-serif;
  cursor: pointer;

  &:hover {
    background-color: #333;
  }
`;

const FooterIndicator = styled.footer`
  border-radius: 2.5px;
  border: 1px solid #d9d9d9;
  background-color: rgba(217, 217, 217, 0.85);
  margin-top: 166px;
  width: 134px;
  height: 5px;
`;

export default IdInputPage;
