/** @jsxImportSource @emotion/react */
import { css } from '@emotion/react';

export const boxStyle = css`
  border-radius: 16px;
  background-color: #fff;
  display: flex;
  max-width: 360px;
  flex-direction: column;
  justify-content: center;
  font-size: 18px;
  font-weight: 600;
`;

export const divStyle = css`
  display: flex;
  flex-direction: column;
  justify-content: flex-end;
  overflow: hidden;
  position: relative;
  aspect-ratio: 0.46;
  margin-top: 5px;
  width: 100%;
  padding-top: 50px;
`;

export const imgStyle = css`
  position: absolute;
  inset: 0;
  height: 100%;
  width: 100%;
  object-fit: cover;
  object-position: center;
`;

export const div2Style = css`
  position: relative;
  text-shadow: 0px 4px 20px rgba(0, 0, 0, 0.25);
  font-family: Poppins, sans-serif;
  filter: drop-shadow(0px 4px 20px rgba(0, 0, 0, 0.25));
  background-color: #171717;
  border-radius: 50%;
  align-self: center;
  margin-top: 336px;
  width: 88px;
  justify-content: center;
  align-items: center;
  height: 88px;
  color: #fff;
  white-space: nowrap;
  padding: 0 15px;
`;

export const div3Style = css`
  position: relative;
  position: center;
  background: linear-gradient(0deg, #fff 69.44%, rgba(255, 255, 255, 0) 100%);
  z-index: 10;
  display: flex;
  margin-bottom: -5px;
  width: 95%;
  flex-direction: column;
  color: #000;
  text-align: center;
  line-height: 144%;
  padding: 50px 16px 12px;
`;

export const div4Style = css`
  align-self: center;
  margin-top: 58px;
  font: 26px Source Sans Pro, -apple-system, Roboto, Helvetica, sans-serif;
`;

export const div5Style = css`
  font-family: Source Sans Pro, -apple-system, Roboto, Helvetica, sans-serif;
  justify-content: center;
  align-items: center;
  border-radius: 100px;
  background-color: #000;
  margin-top: 29px;
  color: #fff;
  white-space: nowrap;
  padding: 9px 60px;
`;

export const div6Style = css`
  font-family: Source Sans Pro, -apple-system, Roboto, Helvetica, sans-serif;
  justify-content: center;
  align-items: center;
  border-radius: 100px;
  background-color: rgba(221, 221, 221, 0.5);
  margin-top: 8px;
  color: #2b2b2b;
  white-space: nowrap;
  padding: 9px 60px;
`;

export const div7Style = css`
  align-self: center;
  margin-top: 77px;
  font: 400 11px/19px Source Sans Pro, -apple-system, Roboto, Helvetica, sans-serif;
`;
